mapper.select:
select
id, name, slug, description, created_at, updated_at
from tb_category


        and id = #{id}


        and name = #{name}


        and slug = #{slug}


        and description = #{description}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from tb_category


        and id = #{id}


        and name = #{name}


        and slug = #{slug}


        and description = #{description}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
